import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './App.css';

const WindowsClassicAuthModal = ({ onClose, onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${window.API_BASE_URL || 'http://localhost'}/api/v1/token`, {
        username,
        password
      });
      onLogin(response.data.token);
      onClose();
    } catch (err) {
      setError('Ошибка авторизации. Проверьте логин и пароль.');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="windows-modal">
        <div className="modal-header">
          <h3>Авторизация</h3>
          <button onClick={onClose} className="modal-close">✕</button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Логин:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="windows-input"
            />
          </div>
          <div className="form-group">
            <label>Пароль:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="windows-input"
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" className="windows-button">Авторизоваться</button>
        </form>
      </div>
    </div>
  );
};

const JsonTableView = ({ endpoint }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      if (!endpoint) return;

      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        const response = await axios.get(endpoint, config);
        setData(response.data);
        setError('');
      } catch (err) {
        setError(`Ошибка загрузки данных: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [endpoint]);

  const renderTable = (data) => {
    if (!data) return null;

    if (Array.isArray(data)) {
      // Find all unique keys
      const headers = {};
      data.forEach(item => {
        if (typeof item === 'object' && item !== null) {
          Object.keys(item).forEach(key => {
            headers[key] = true;
          });
        }
      });

      const headerKeys = Object.keys(headers);

      return (
        <table className="windows-table">
          <thead>
            <tr>
              {headerKeys.map(key => (
                <th key={key}>{key}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index}>
                {headerKeys.map(key => (
                  <td key={`${index}-${key}`}>
                    {typeof item[key] === 'object' 
                      ? JSON.stringify(item[key]) 
                      : String(item[key] ?? '')}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      );
    } else if (typeof data === 'object' && data !== null) {
      return (
        <table className="windows-table">
          <tbody>
            {Object.entries(data).map(([key, value]) => (
              <tr key={key}>
                <th>{key}</th>
                <td>
                  {typeof value === 'object' 
                    ? JSON.stringify(value) 
                    : String(value ?? '')}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    }

    return <div>{String(data)}</div>;
  };

  return (
    <div className="tab-content">
      <div className="endpoint-url">
        <strong>Endpoint:</strong> {endpoint}
      </div>
      {loading && <div className="loading">Загрузка...</div>}
      {error && <div className="error-message">{error}</div>}
      {data && renderTable(data)}
    </div>
  );
};

const Tab = ({ children, isActive, onClick }) => (
  <button
    className={`windows-tab ${isActive ? 'active' : ''}`}
    onClick={onClick}
  >
    {children}
  </button>
);

const App = () => {
  const [tabs, setTabs] = useState([
    { id: 1, name: 'Таблица 1', endpoint: '' }
  ]);
  const [activeTab, setActiveTab] = useState(1);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token')
  );
  const [apiBaseUrl, setApiBaseUrl] = useState(
    localStorage.getItem('apiBaseUrl') || 'http://localhost'
  );

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  const addTab = () => {
    const newTabId = tabs.length > 0 ? Math.max(...tabs.map(tab => tab.id)) + 1 : 1;
    setTabs([...tabs, { id: newTabId, name: `Таблица ${newTabId}`, endpoint: '' }]);
    setActiveTab(newTabId);
  };

  const removeTab = (id) => {
    if (tabs.length <= 1) return;
    const newTabs = tabs.filter(tab => tab.id !== id);
    setTabs(newTabs);
    if (activeTab === id) {
      setActiveTab(newTabs[0].id);
    }
  };

  const updateTabEndpoint = (id, endpoint) => {
    setTabs(tabs.map(tab => 
      tab.id === id ? { ...tab, endpoint } : tab
    ));
  };

  const saveApiSettings = () => {
    localStorage.setItem('apiBaseUrl', apiBaseUrl);
    window.API_BASE_URL = apiBaseUrl;
    alert('Настройки сохранены!');
  };

  return (
    <Router>
      <div className="windows-app">
        <div className="windows-title-bar">
          <div className="windows-title">JSON Tables Viewer</div>
          <div className="windows-controls">
            <button className="windows-button" onClick={() => setShowAuthModal(true)}>
              Настройки API
            </button>
            <button 
              className={`windows-button ${isAuthenticated ? 'auth-success' : 'auth-failure'}`}
              onClick={isAuthenticated ? handleLogout : () => setShowAuthModal(true)}
            >
              {isAuthenticated ? 'Выйти' : 'Войти'}
            </button>
          </div>
        </div>

        <div className="windows-tab-bar">
          {tabs.map(tab => (
            <div key={tab.id} className="tab-container">
              <Tab
                isActive={activeTab === tab.id}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.name}
              </Tab>
              {tabs.length > 1 && (
                <button className="tab-close" onClick={() => removeTab(tab.id)}>
                  ✕
                </button>
              )}
            </div>
          ))}
          <button className="windows-tab-add" onClick={addTab}>
            +
          </button>
        </div>

        <div className="windows-content">
          <Routes>
            {tabs.map(tab => (
              <Route
                key={tab.id}
                path={`/tab/${tab.id}`}
                element={
                  <div className="tab-panel">
                    <div className="endpoint-input">
                      <input
                        type="text"
                        value={tab.endpoint}
                        onChange={(e) => updateTabEndpoint(tab.id, e.target.value)}
                        placeholder="Введите URL для получения JSON"
                        className="windows-input"
                      />
                      <button 
                        className="windows-button"
                        onClick={() => updateTabEndpoint(tab.id, `${apiBaseUrl}${tab.endpoint}`)}
                      >
                        Загрузить
                      </button>
                    </div>
                    <JsonTableView endpoint={tab.endpoint} />
                  </div>
                }
              />
            ))}
          </Routes>
        </div>

        {showAuthModal && (
          <WindowsClassicAuthModal
            onClose={() => setShowAuthModal(false)}
            onLogin={handleLogin}
          />
        )}

        {/* API Settings Modal */}
        {showAuthModal && (
          <div className="modal-overlay">
            <div className="windows-modal">
              <div className="modal-header">
                <h3>Настройки API</h3>
                <button onClick={() => setShowAuthModal(false)} className="modal-close">✕</button>
              </div>
              <div className="form-group">
                <label>Базовый URL API:</label>
                <input
                  type="text"
                  value={apiBaseUrl}
                  onChange={(e) => setApiBaseUrl(e.target.value)}
                  className="windows-input"
                />
              </div>
              <div className="modal-buttons">
                <button className="windows-button" onClick={saveApiSettings}>
                  Сохранить
                </button>
                <button className="windows-button" onClick={() => setShowAuthModal(false)}>
                  Отмена
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
