import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import JsonTableView from './JsonTableView';
import Tab from './Tab';

const TabsManager = ({ tabs, activeTab, onTabChange, onTabAdd, onTabRemove, baseUrl }) => {
  return (
    <>
      <div className="windows-tab-bar">
        {tabs.map(tab => (
          <Tab
            key={tab.id}
            isActive={activeTab === tab.id}
            onClick={() => onTabChange(tab.id)}
            onClose={tabs.length > 1 ? () => onTabRemove(tab.id) : null}
          >
            {tab.name}
          </Tab>
        ))}
        <button className="windows-tab-add" onClick={onTabAdd}>
          +
        </button>
      </div>

      <div className="windows-content">
        <Routes>
          {tabs.map(tab => (
            <Route
              key={tab.id}
              path={`/tab/${tab.id}`}
              element={
                <div className="tab-panel">
                  <div className="endpoint-controls">
                    <input
                      type="text"
                      value={tab.endpoint}
                      onChange={(e) => tab.onEndpointChange(e.target.value)}
                      placeholder="Введите endpoint (например: /api/data)"
                      className="windows-input"
                    />
                    <button 
                      className="windows-button"
                      onClick={tab.onLoadData}
                    >
                      Загрузить
                    </button>
                  </div>
                  <JsonTableView endpoint={tab.endpoint} baseUrl={baseUrl} />
                </div>
              }
            />
          ))}
        </Routes>
      </div>
    </>
  );
};

export default TabsManager;
