import React from 'react';
import { Routes, Route } from 'react-router-dom';
import JsonTableView from './JsonTableView';
import Tab from './Tab';

const TabsManager = ({ 
  tabs, 
  activeTab, 
  onTabChange, 
  onTabAdd, 
  onTabRemove,
  onEndpointChange,
  onLoadData,
  onApiSettingsChange
}) => {
  const activeTabData = tabs.find(tab => tab.id === activeTab) || tabs[0];

  return (
    <>
      <div className="windows-tab-bar">
        {tabs.map(tab => (
          <Tab
            key={tab.id}
            isActive={activeTab === tab.id}
            onClick={() => onTabChange(tab.id)}
            onClose={tabs.length > 1 ? () => onTabRemove(tab.id) : null}
          >
            {tab.name}
          </Tab>
        ))}
        <button className="windows-tab-add" onClick={onTabAdd}>
          +
        </button>
      </div>

      {/* Новая панель управления для активной вкладки */}
      <div className="tab-controls">
        <div className="tab-controls-group tab-controls-url">
          <span className="tab-controls-label">URL:</span>
          <input
            type="text"
            value={activeTabData.endpoint}
            onChange={(e) => onEndpointChange(activeTabData.id, e.target.value)}
            placeholder="Полный URL (например: http://localhost/api/data)"
            className="windows-input"
          />
        </div>
        
        <div className="tab-controls-group">
          <button 
            className="windows-button"
            onClick={() => onLoadData(activeTabData.id)}
          >
            Загрузить
          </button>
          <button 
            className="windows-button"
            onClick={() => {
              const newBaseUrl = prompt('Базовый URL:', activeTabData.baseUrl || 'http://localhost');
              if (newBaseUrl) onApiSettingsChange(activeTabData.id, newBaseUrl);
            }}
          >
            Настройки
          </button>
        </div>
      </div>

      <div className="windows-content">
        <Routes>
          {tabs.map(tab => (
            <Route
              key={tab.id}
              path={`/tab/${tab.id}`}
              element={
                <JsonTableView 
                  endpoint={tab.endpoint} 
                  baseUrl={tab.baseUrl}
                  data={tab.data}
                  loading={tab.loading}
                  error={tab.error}
                />
              }
            />
          ))}
        </Routes>
      </div>
    </>
  );
};

export default TabsManager;
