import React, { useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import AuthModal from './components/AuthModal';
import TabsManager from './components/TabsManager';
import './App.css';

const App = () => {
  const [tabs, setTabs] = useState([
    { 
      id: 1, 
      name: 'Таблица 1', 
      endpoint: '',
      onEndpointChange: (newEndpoint) => updateTabEndpoint(1, newEndpoint),
      onLoadData: () => loadTabData(1)
    }
  ]);
  const [activeTab, setActiveTab] = useState(1);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token')
  );
  const [baseUrl, setBaseUrl] = useState(
    localStorage.getItem('apiBaseUrl') || 'http://localhost'
  );

  const handleLogin = (token, apiUrl) => {
    localStorage.setItem('token', token);
    localStorage.setItem('authUrl', apiUrl);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('authUrl');
    setIsAuthenticated(false);
  };

  const addTab = () => {
    const newTabId = tabs.length > 0 ? Math.max(...tabs.map(tab => tab.id)) + 1 : 1;
    const newTab = {
      id: newTabId,
      name: `Таблица ${newTabId}`,
      endpoint: '',
      onEndpointChange: (newEndpoint) => updateTabEndpoint(newTabId, newEndpoint),
      onLoadData: () => loadTabData(newTabId)
    };
    setTabs([...tabs, newTab]);
    setActiveTab(newTabId);
  };

  const removeTab = (id) => {
    if (tabs.length <= 1) return;
    const newTabs = tabs.filter(tab => tab.id !== id);
    setTabs(newTabs);
    if (activeTab === id) {
      setActiveTab(newTabs[0].id);
    }
  };

  const updateTabEndpoint = (id, endpoint) => {
    setTabs(tabs.map(tab => 
      tab.id === id ? { ...tab, endpoint } : tab
    ));
  };

  const loadTabData = (id) => {
    updateTabEndpoint(id, tabs.find(t => t.id === id).endpoint);
  };

  return (
    <Router>
      <div className="windows-app">
        <div className="windows-title-bar">
          <div className="windows-title">JSON Tables Viewer</div>
          <div className="windows-controls">
            <button 
              className={`windows-button ${isAuthenticated ? 'auth-success' : 'auth-failure'}`}
              onClick={isAuthenticated ? handleLogout : () => setShowAuthModal(true)}
            >
              {isAuthenticated ? 'Выйти' : 'Войти'}
            </button>
          </div>
        </div>

        <TabsManager
          tabs={tabs}
          activeTab={activeTab}
          onTabChange={setActiveTab}
          onTabAdd={addTab}
          onTabRemove={removeTab}
          baseUrl={baseUrl}
        />

        {showAuthModal && (
          <AuthModal
            onClose={() => setShowAuthModal(false)}
            onLogin={handleLogin}
          />
        )}
      </div>
    </Router>
  );
};

export default App;
