import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import AuthModal from './components/AuthModal';
import TabsManager from './components/TabsManager';
import './App.css';

const App = () => {
  const [tabs, setTabs] = useState([
    { 
      id: '1', 
      name: 'Таблица 1', 
      endpoint: '',
      data: null,
      loading: false,
      error: null
    }
  ]);
  const [activeTab, setActiveTab] = useState('1');
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token')
  );

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setIsAuthenticated(true);
    setShowAuthModal(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  const addTab = () => {
    const newTabId = Date.now().toString();
    setTabs([...tabs, { 
      id: newTabId, 
      name: `Таблица ${tabs.length + 1}`, 
      endpoint: '',
      data: null,
      loading: false,
      error: null
    }]);
    setActiveTab(newTabId);
  };

  const removeTab = (id) => {
    if (tabs.length <= 1) return;
    const newTabs = tabs.filter(tab => tab.id !== id);
    setTabs(newTabs);
    if (activeTab === id) {
      setActiveTab(newTabs[0].id);
    }
  };

  const updateEndpoint = (id, endpoint) => {
    setTabs(tabs.map(tab => 
      tab.id === id ? { ...tab, endpoint } : tab
    ));
  };

  const loadData = async (id) => {
    const tab = tabs.find(t => t.id === id);
    if (!tab || !tab.endpoint) return;

    setTabs(tabs.map(t => 
      t.id === id ? { ...t, loading: true, error: null, data: null } : t
    ));

    try {
      const token = localStorage.getItem('token');
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      const response = await axios.get(tab.endpoint, config);
      setTabs(tabs.map(t => 
        t.id === id ? { ...t, data: response.data, loading: false } : t
      ));
    } catch (err) {
      setTabs(tabs.map(t => 
        t.id === id ? { 
          ...t, 
          error: `Ошибка: ${err.response?.data?.message || err.message}`,
          loading: false 
        } : t
      ));
    }
  };

  return (
    <div className="windows-app">
      <div className="windows-title-bar">
        <div className="windows-title">JSON Tables Viewer</div>
        <div className="windows-controls">
          <button 
            className={`windows-button ${isAuthenticated ? 'auth-success' : 'auth-failure'}`}
            onClick={isAuthenticated ? handleLogout : () => setShowAuthModal(true)}
          >
            {isAuthenticated ? 'Выйти' : 'Войти'}
          </button>
        </div>
      </div>

      <Router>
        <Routes>
          <Route path="/" element={<Navigate to={`/tab/${activeTab}`} replace />} />
          <Route path="/tab/:tabId" element={
            <TabsManager
              tabs={tabs}
              activeTab={activeTab}
              onTabChange={setActiveTab}
              onTabAdd={addTab}
              onTabRemove={removeTab}
              onEndpointChange={updateEndpoint}
              onLoadData={loadData}
            />
          } />
        </Routes>
      </Router>

      {showAuthModal && (
        <AuthModal
          onClose={() => setShowAuthModal(false)}
          onLogin={handleLogin}
        />
      )}
    </div>
  );
};

export default App;
