import React from 'react';
import { Routes, Route } from 'react-router-dom';
import JsonTableView from './JsonTableView';
import Tab from './Tab';

const TabsManager = ({ 
  tabs, 
  activeTab, 
  onTabChange, 
  onTabAdd, 
  onTabRemove,
  onEndpointChange,
  onLoadData,
  onApiSettingsChange
}) => {
  return (
    <>
      <div className="windows-tab-bar">
        {tabs.map(tab => (
          <Tab
            key={tab.id}
            isActive={activeTab === tab.id}
            onClick={() => onTabChange(tab.id)}
            onClose={tabs.length > 1 ? () => onTabRemove(tab.id) : null}
          >
            {tab.name}
          </Tab>
        ))}
        <button className="windows-tab-add" onClick={onTabAdd}>
          +
        </button>
      </div>

      <div className="windows-content">
        <Routes>
          {tabs.map(tab => (
            <Route
              key={tab.id}
              path={`/tab/${tab.id}`}
              element={
                <div className="tab-panel">
                  <div className="endpoint-controls">
                    <input
                      type="text"
                      value={tab.endpoint}
                      onChange={(e) => onEndpointChange(tab.id, e.target.value)}
                      placeholder="Полный URL (например: http://localhost/api/data)"
                      className="windows-input"
                    />
                    <button 
                      className="windows-button"
                      onClick={() => onLoadData(tab.id)}
                    >
                      Загрузить
                    </button>
                    <button 
                      className="windows-button"
                      onClick={() => onApiSettingsChange(tab.id, prompt('Введите базовый URL:', tab.baseUrl || 'http://localhost'))}
                    >
                      Настройки
                    </button>
                  </div>
                  <JsonTableView 
                    endpoint={tab.endpoint} 
                    baseUrl={tab.baseUrl}
                    data={tab.data}
                    loading={tab.loading}
                    error={tab.error}
                  />
                </div>
              }
            />
          ))}
        </Routes>
      </div>
    </>
  );
};

export default TabsManager;
